import express from 'express';
import APIToolkit from 'apitoolkit-express';
const { Configuration, OpenAIApi} = require('openai');
// const configuration = new Configuration({apiKey: "xvZIepZMayszlIEagqZsGTsd9GLBTtSevOm+gO5dpjgC9IiT"});
const configuration = new Configuration({apiKey:xvZIepZMayszlIEagqZsGTsd9GLBTtSevOm+gO5dpjgC9IiT})
app.use(configuration.expressMiddleware);
const openai = new OpenAIApi(configuration);

// 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
const app = express();

export async function sendMsgToOpenAi(message){
    const res = await openai.createCompletion({
        model: 'text-davinci-003',
        prompt: message,
        temperature: 0.7,
        max_token: 256,
        top_p:1,
        freequency_penalty:0,
        presense_penalty:0
    });
    return res.data.choices[0].text;
}
